let parsed = JSON.parse(FRAME_API_INFO);

let frames = Object.keys(parsed);

let abilityIconBaseURIPrefix = "https://semlar.com/textures/_Lotus_Interface_Icons_Abilities_";
let abilityIconBaseURISuffix = "";

let implemented = [
    "Excalibur"
];

function SetFrame(frameName) {
    let frame = parsed[frameName];

    console.log(frame.abilities);

    $("title").text(frame.name);

    $("#frameName").text(frame.name);
    $("#frameHealth").text(frame.health);
    $("#frameShield").text(frame.shield);
    $("#frameArmour").text(frame.armour);
    $("#frameEnergy").text(frame.energy);

    frameIcon = "https://media.overframe.gg/v2/256x/Lotus/Interface/Icons/StoreIcons/Warframes/" + frame.name + ".png.webp";
    primeIcon = "https://media.overframe.gg/v2/256x/Lotus/Interface/Icons/StoreIcons/Primes/" + frame.name + "Prime.png.webp";

    abilityIcons = [];

    console.log(frame.name);

    swapIconFunc = "SwapImg('#wfIcon', '" + frameIcon + "', '" + primeIcon + "')"
    
    if (Object.keys(frame).includes("primeInfo")) { swapIconFunc = "javascript:console.log('No Prime icon.')"; }
    
    $("#frameIcon").attr("onclick", swapIconFunc);

    $("#frameIcon img").attr("src", frameIcon);

    for (let i = 0; i < 4; i++) {
        if (implemented.includes(frame.name)) {
            abilityIcons.push("../resources/images/abilityIcons/" + frame.abilities[i].imageName);
        }
        else {
            words = frame.abilities[0].imageName.split(/[-]+/);
            abilName = "";

            for (let i = 0; i < words.length; i++) {
                const word = words[i];
                abilName += word.charAt(0).toUpperCase() + word.slice(1);
            }

            abilityIcons.push("https://semlar.com/textures/_Lotus_Interface_Icons_Abilities_" + abilName);
        }
    }

    $(".passive").contents().filter(function(){ return this.nodeType == 3; }).last()
        .replaceWith(frame.passiveDescription);

    $("#ability1 div h3").text(frame.abilities[0].name);
    $("#ability1 div img").attr("src", abilityIcons[0]);
    $("#ability1").contents().filter(function(){ return this.nodeType == 3; }).last()
        .replaceWith(frame.abilities[0].description);

    $("#ability2 div h3").text(frame.abilities[1].name);
    $("#ability2 div img").attr("src", abilityIcons[1]);
    $("#ability2").contents().filter(function(){ return this.nodeType == 3; }).last()
        .replaceWith(frame.abilities[1].description);

    $("#ability3 div h3").text(frame.abilities[2].name);
    $("#ability3 div img").attr("src", abilityIcons[2]);
    $("#ability3").contents().filter(function(){ return this.nodeType == 3; }).last()
        .replaceWith(frame.abilities[2].description);

    $("#ability4 div h3").text(frame.abilities[3].name);
    $("#ability4 div img").attr("src", abilityIcons[3]);
    $("#ability4").contents().filter(function(){ return this.nodeType == 3; }).last()
        .replaceWith(frame.abilities[3].description);
}